INSERT INTO assertion1 (researchStatementLabel, assertType, objectURI, valueURI, label, homepage, source, dateAnnotated, whoAnnotated, evidence, evidenceRole, evidenceSource, evidenceType, evidenceStatement, object, precip, numericVal, contVal, evidenceVal, objectDose, precipDose, numOfSubjects) VALUES ('ziprasidone_substrate_of_cyp3a4', 'substrate_of', 'http://www4.wiwiss.fu-berlin.de/drugbank/resource/drugs/DB00246', 'http://purl.obolibrary.org/obo/PR_000006130', '', 'http://dbmi-icode-01.dbmi.pitt.edu/dikb-evidence/ziprasidone_substrate_of_cyp3a4.html', 'DIKB', '2009-06-19 12:45:00', 'boycer', 'https://dbmi-icode-01.dbmi.pitt.edu/dikb/resource/Evidence/1271', 'support', 'http://www.ncbi.nlm.nih.gov/pubmed/10771452', 'http://dbmi-icode-01.dbmi.pitt.edu/dikb-evidence/DIKB_evidence_ontology_v1.3.owl#EV_EX_Met_Enz_ID_Cyp450_Hum_Recom', '

