INSERT INTO assertion1 (researchStatementLabel, assertType, objectURI, valueURI, label, homepage, source, dateAnnotated, whoAnnotated, evidence, evidenceRole, evidenceSource, evidenceType, evidenceStatement, object, precip, numericVal, contVal, evidenceVal, objectDose, precipDose, numOfSubjects) VALUES ('clarithromycin_inhibits_cyp3a4', 'inhibits', 'http://www4.wiwiss.fu-berlin.de/drugbank/resource/drugs/DB01211', 'http://purl.obolibrary.org/obo/PR_000006130', '', 'http://dbmi-icode-01.dbmi.pitt.edu/dikb-evidence/clarithromycin_inhibits_cyp3a4.html', 'DIKB', '2010-01-11 16:45:00', 'boycer', 'https://dbmi-icode-01.dbmi.pitt.edu/dikb/resource/Evidence/863', 'support', 'http://dbmi-icode-01.dbmi.pitt.edu/linkedSPLs/resource/struc', 'http://dbmi-icode-01.dbmi.pitt.edu/dikb-evidence/DIKB_evidence_ontology_v1.3.owl#Non_traceable_Drug_Label_Statement', 'Co-administration of clarithromycin, known to inhibit CYP3A, and a drug primarily metabolized by CYP3A may be associated with elevations in drug concentrations that could increase or prolong both therapeutic and adverse effects of the concomitant drug.

Clarithromycin should be used with caution in patients receiving treatment with other drugs known to be CYP3A enzyme substrates, especially if the CYP3A substrate has a narrow safety margin (e.g., carbamazepine) and/or the substrate is extensively metabolized by this enzyme. Dosage adjustments may be considered, and when possible, serum concentrations of drugs primarily metabolized by CYP3A should be monitored closely in patients concurrently receiving clarithromycin.', '', '', 0, '', 0, 0, 0, 0);
