INSERT INTO assertion1 (researchStatementLabel, assertType, objectURI, valueURI, label, homepage, source, dateAnnotated, whoAnnotated, evidence, evidenceRole, evidenceSource, evidenceType, evidenceStatement, object, precip, numericVal, contVal, evidenceVal, objectDose, precipDose, numOfSubjects) VALUES ('paliperidone_inhibits_cyp3a4', 'inhibits', 'http://www4.wiwiss.fu-berlin.de/drugbank/resource/drugs/DB01267', 'http://purl.obolibrary.org/obo/PR_000006130', '', 'http://dbmi-icode-01.dbmi.pitt.edu/dikb-evidence/paliperidone_inhibits_cyp3a4.html', 'DIKB', '', 'boycer', 'https://dbmi-icode-01.dbmi.pitt.edu/dikb/resource/Evidence/109', 'refute', 'http://dbmi-icode-01.dbmi.pitt.edu/linkedSPLs/page/structure', 'http://dbmi-icode-01.dbmi.pitt.edu/dikb-evidence/DIKB_evidence_ontology_v1.3.owl#Non_traceable_Drug_Label_Statement', 'Paliperidone is not expected to cause clinically important pharmacokinetic interactions with drugs that are metabolized by cytochrome P450 isozymes. In vitro studies in human liver microsomes showed that paliperidone does not substantially inhibit the metabolism of drugs metabolized by cytochrome P450 isozymes, including CYP1A2, CYP2A6, CYP2C8/9/10, CYP2D6, CYP2E1, CYP3A4, and CYP3A5. Therefore, paliperidone is not expected to inhibit clearance of drugs that are metabolized by these metabolic pathways in a clinically relevant manner. Paliperidone is also not expected to have enzyme inducing properties.', '', '', 0, '', 0, 0, 0, 0);
