INSERT INTO assertion1 (researchStatementLabel, assertType, objectURI, valueURI, label, homepage, source, dateAnnotated, whoAnnotated, evidence, evidenceRole, evidenceSource, evidenceType, evidenceStatement, object, precip, numericVal, contVal, evidenceVal, objectDose, precipDose, numOfSubjects) VALUES ('omeprazole_inhibits_cyp2d6', 'inhibits', 'http://www4.wiwiss.fu-berlin.de/drugbank/resource/drugs/DB00338', 'http://purl.obolibrary.org/obo/PR_000006121', '', 'http://dbmi-icode-01.dbmi.pitt.edu/dikb-evidence/omeprazole_inhibits_cyp2d6.html', 'DIKB', '2009-09-23 11:38:00', 'boycer', 'https://dbmi-icode-01.dbmi.pitt.edu/dikb/resource/Evidence/155', 'refute', 'http://www.ncbi.nlm.nih.gov/pubmed/15258107', 'http://dbmi-icode-01.dbmi.pitt.edu/dikb-evidence/DIKB_evidence_ontology_v1.3.owl#EV_EX_Met_Enz_Inhibit_Cyp450_Hum_Microsome', '
Enzyme system: human liver microsomes:

NADPH added: yes

substrate used: bufuralol

reaction: bufuralol 1''hydroxylation

IC50 &gt; 200micM (see Table 3)

