INSERT INTO assertion1 (researchStatementLabel, assertType, objectURI, valueURI, label, homepage, source, dateAnnotated, whoAnnotated, evidence, evidenceRole, evidenceSource, evidenceType, evidenceStatement, object, precip, numericVal, contVal, evidenceVal, objectDose, precipDose, numOfSubjects) VALUES ('citalopram_inhibits_cyp2c9', 'inhibits', 'http://www4.wiwiss.fu-berlin.de/drugbank/resource/drugs/DB00215', 'http://purl.obolibrary.org/obo/PR_000006120', '', 'http://dbmi-icode-01.dbmi.pitt.edu/dikb-evidence/citalopram_inhibits_cyp2c9.html', 'DIKB', '2010-02-12 14:53:00', 'boycer', 'https://dbmi-icode-01.dbmi.pitt.edu/dikb/resource/Evidence/213', 'refute', 'http://www.ncbi.nlm.nih.gov/pubmed/9278211', 'http://dbmi-icode-01.dbmi.pitt.edu/dikb-evidence/DIKB_evidence_ontology_v1.3.owl#EV_PK_DDI_RCT', '

